import React, {useState} from 'react';
// import {sendFileToFirebase, getFileFromFirebase} from '../services/firebaseStorage.service';
import {useHistory } from 'react-router-dom';
// import { getDownloadURL } from "firebase/storage";

function BoardModerator() {
    
    const [selectedFile, setSelectedFile] = useState();
	const [isSelected, setIsFilePicked] = useState(false);
    let history = useHistory();
	//const downloadUrl="";
    const changeHandler = (event) => {
		setSelectedFile(event.target.files[0]);
		setIsFilePicked(true);
	};

    const handleSubmission =  () => {
        
        // const downloadUrl = sendFileToFirebase(selectedFile);
		
		// setIsFilePicked(false);
		
		
		// console.log( downloadUrl.snapshot.ref._location.path_);

		// getFileFromFirebase(downloadUrl.snapshot.ref._location.path_);






		// getDownloadURL(downloadUrl.snapshot.ref.).then((url) => {
		// 	console.log('File available at', url);

			
		// });
		
		
		history.push("/Arms");


	};
    return (
        <div className="container text-center">
            <h1>Exercies Set</h1>
			<div>
			<br></br>
				<button className="btn btn-secondary btn-lg" onClick={handleSubmission}>Arms</button>
				<br></br>
			</div>
			<div>
			<br></br>
				<button className="btn btn-secondary btn-lg" onClick={handleSubmission}>Chest</button>
				<br></br>
			</div>
			<div>
			<br></br>
				<button className="btn btn-secondary btn-lg" onClick={handleSubmission}>Back</button>
				<br></br>
			</div>
			<div>
			<br></br>
				<button className="btn btn-secondary btn-lg" onClick={handleSubmission}>Legs</button>
				<br></br>
			</div>
			<div>
			<br></br>
				<button className="btn btn-secondary btn-lg" onClick={handleSubmission}>Cardio</button>
				<br></br>
			</div>
			
        </div>
    )
}

export default BoardModerator
