import React, {useState} from 'react'
import "./styling/forgotPassword.css"
import AuthService from "../services/auth.service";




function Forgetpassword() {


    const [enteredEmail,setEnteredEmail] = useState("");
    const [isEmailsent, setIsEmailsent] = useState(undefined);
    const [isError, setIsError] = useState();
    const [error, setError] = useState(undefined)


    const emailChangeHandler = (e) => {
        setEnteredEmail(e.target.value);
    };

    const submitHandler = (e) => {
        e.preventDefault();
        // console.log("CLICKED")
        // console.log("EMAIL: "+ enteredEmail)
        AuthService.forgotPassword(
            enteredEmail
        
            ).then((response) => {
            setIsError(false);
            console.log(response)
            setIsEmailsent("Please verify you Email");
            
            
            
          }).catch(error => {
              setIsError(true)
              setError(JSON.stringify(error.response.data));
              console.log( error.response.data );
          })

        

    }
    return (
        <div>
        <h1>Forgot Password</h1>
            <form onSubmit={submitHandler}>
               {isError? <div className="alert alert-success" role="alert">          
                {isEmailsent}
                </div>:<div className="alert alert-success" role="alert">          
                {error}
                </div>}

                
                <label>Email</label>
                <input type="text" value = {enteredEmail} onChange = {emailChangeHandler}></input>
              
              <div className="button">

            <button
                type="submit"
                className="btn btn-secondary btn btn-primary btn-lg"
              > Forgot Password</button>
            </div>
            </form>
            
        </div>
    )
}

export default Forgetpassword
