import React, {useState} from 'react'
import "./styling/forgotPassword.css"
import AuthService from "../services/auth.service";




function Forgetpassword() {


    const [enteredEmail,setEnteredEmail] = useState("");


    const emailChangeHandler = (e) => {
        setEnteredEmail(e.target.value);
    };

    const submitHandler = (e) => {
        e.preventDefault();
        console.log("EMAIL: "+ enteredEmail)
        AuthService.forgotPassword(enteredEmail).then(() => {
            this.props.history.push("/confirmation");
            window.location.reload();
          },error => {
              console.log("Error: "+ error );
          })

        

    }
    return (
        <div>
        <h1>Forgot Password</h1>
            <form onSubmit={submitHandler}>
                <label>Email</label>
                <input type="text" value = {enteredEmail} onChange = {emailChangeHandler}></input>
              
            </form>
            <div className="button">

            <butto
                type="submit"
                className="btn btn-secondary btn btn-primary btn-lg"
              > Forgot Password</butto>
            </div>
        </div>
    )
}

export default Forgetpassword
