// import { initializeApp } from "firebase/app";

import * as firebase from 'firebase';

import { initializeApp } from "firebase/app";
import { getStorage } from "firebase/storage";


// Firebase configuration

const firebaseConfig = {
    apiKey: "AIzaSyDKqs2zl_2KN6NTJE3ISiQqinsW8-uc4TI",
    authDomain: "ecse428-a311f.firebaseapp.com",
    projectId: "ecse428-a311f",
    storageBucket: "ecse428-a311f.appspot.com",
    messagingSenderId: "647846550145",
    appId: "1:647846550145:web:f59d1b0c1ebd4071f38b80"
  };

// Initialize Firebase
// const app = initializeApp(firebaseConfig);

// firebase.initializeApp(firebaseConfig);
const firebaseApp = initializeApp(firebaseConfig);
const storage = getStorage(firebaseApp);

// var storage = firebase.storage();

// We use this storageRef vairable to access firebase storage
var storageRef = storage.ref();

// Create a random string as a filename to use in firebase, we can also create an increasing int variable in the database to use it as a filename so that we can make the filenames unique
var fileName = Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);

//String concatination for the path, We need to support multiple extensions! 

var path = 'files' + '/' + fileName + '.dcm';

// Pointer to our created fileName
var fileRef = storageRef.child(path);

// Method for sending a file to firease storage (If we want, we can change the method's signature to support a filename parameter etc..)
// In this method I implemented it using File interface https://developer.mozilla.org/en-US/docs/Web/API/File we can also use Blob, byte array, etc.

export function sendFileToFirebase(file) {
    console.log("ready to send file to firebase");

    var task = fileRef.put(file);

    // Here we listen for multiple state changes and act based on the result
    task.on(firebase.storage.TaskEvent.STATE_CHANGED, 
        (snapshot) => {
            // Get the progress by the number of bytes uploaded and the total number of bytes to be uploaded
            var progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            console.log('Upload is ' + progress + '% done');
            switch (snapshot.state) {
                case firebase.storage.TaskState.PAUSED:
                    console.log('Upload is paused');
                    break;
                case firebase.storage.TaskState.RUNNING: 
                    console.log('Upload is running');
                    break;
            }
        },
        (error) => {
            console.log(error.code);
        },
        () => {
            // Upload completed successfully, now we can get the download URL
            task.snapshot.ref.getDownloadURL().then((downloadURL) => {
                console.log('File is successfully uploaded to: ', downloadURL);
            });
        }
    );
}

// This method is for downloading a file from firebase storage, we need an argument as a path so that we can get the file. Also, we can turn the file into image if needed.
//fullPath is the path of a file in firebase storage such as files / asd.jpg
export function getFileFromFirebase(fullPath) {
    storageRef.child(fullPath).getDownloadURL()
        .then((url) => {
            // This can be downloaded directly:
            var xhr = new XMLHttpRequest();
            xhr.responseType = 'blob'; // We get the data with blob here instead of File
            xhr.onload = (event) => {
                var blob = xhr.response;
            };
            xhr.open('GET', url);
            xhr.send(); // With .send() we can change the contents of the page without distrupting user and without reloading the page

            // We can turn it into image as well if needed
           // var img = document.getElementById('patientImg');
          //  img.setAttribute('src', url);
        })
        .catch((error) => {
            console.log(error);
        });

}
