import React, {useState} from 'react';
import {sendFileToFirebase, getFileFromFirebase} from '../services/firebaseStorage.service';
import {useHistory } from 'react-router-dom';

function BoardModerator() {
    
    const [selectedFile, setSelectedFile] = useState();
	const [isSelected, setIsFilePicked] = useState(false);
    let history = useHistory();
	let downloadUrl="";
    const changeHandler = (event) => {
		setSelectedFile(event.target.files[0]);
		setIsFilePicked(true);
	};

    const   handleSubmission = () => {
        
        downloadUrl =   sendFileToFirebase(selectedFile);
		setIsFilePicked(false);
		console.log("downloadUrl");
		
		
		//history.push("/result");


	};
    return (
        <div>
            
            <div class="input-group mb-3">
                <div class="input-group-prepend">
                    <span class="input-group-text">Upload</span>
                </div>
                <div class="custom-file">
                    <input type="file" class="custom-file-input" id="inputGroupFile01" onChange={changeHandler}/>
                    <label class="custom-file-label" for="inputGroupFile01">Choose file</label>
                </div>
            </div>

           {/* <input  type="file" name="file" onChange={changeHandler} /> */}
			{isSelected ? (
				<div>
					<p>Filename: {selectedFile.name}</p>
					<p>Filetype: {selectedFile.type}</p>
					<p>Size in bytes: {selectedFile.size}</p>
					<p>
						lastModifiedDate:{' '}
						{selectedFile.lastModifiedDate.toLocaleDateString()}
					</p>
				</div>
			) : (
				<p>Select a file to show details</p>
			)}
			<div>
				<button className="btn btn-secondary btn-lg" onClick={handleSubmission}>Submit</button>
			</div>
        </div>
    )
}

export default BoardModerator
