import React, {useState} from 'react'
import "./styling/forgotPassword.css"
import AuthService from "../services/auth.service";




function Forgetpassword() {


    const [enteredEmail,setEnteredEmail] = useState("");
    const [message, setMessage]= useState(undefined);


    const emailChangeHandler = (e) => {
        setEnteredEmail(e.target.value);
    };

    const submitHandler = (e) => {
        e.preventDefault();
        AuthService.forgotPassword(
            enteredEmail
        
            ).then((response) => {
            console.log(response)
            setMessage("Please verify you Email");
   
          }).catch(error => {
            setMessage(JSON.stringify(error.response.data.message));
          })

        

    }
    return (
        <div>
        <h1>Forgot Password</h1>
            <form onSubmit={submitHandler}>
               {message? <div className="alert alert-success" role="alert">          
                {message}
                </div>:null}
                <label>Email</label>
                <input type="text" value = {enteredEmail} onChange = {emailChangeHandler}></input>
              
              <div className="button">

            <button
                type="submit"
                className="btn btn-secondary btn btn-primary btn-lg"
              > Forgot Password</button>
            </div>
            </form>
            
        </div>
    )
}

export default Forgetpassword
